SELECT post.post_no, post.title, post.created_at, user.name
FROM post LEFT JOIN user ON post.user_no = user.user_no
WHERE post.title LIKE CONCAT('%', #{keyword}, '%') OR user.name LIKE CONCAT('%', #{keyword}, '%')
    LIMIT #{pagesize}

        OFFSET #{offset}

